"use strict";(self.webpackChunkmy_document=self.webpackChunkmy_document||[]).push([[8179],{1576:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Fundamentals","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Battery Management Systems in Electric Vehicles","href":"/BMS/docs/Fundamentals/bms-in-ev","docId":"Fundamentals/bms-in-ev","unlisted":false},{"type":"link","label":"Components of Electric Vehicles","href":"/BMS/docs/Fundamentals/components","docId":"Fundamentals/components","unlisted":false},{"type":"link","label":"Permanent Magnet Synchronous Motors in Automotive Applications","href":"/BMS/docs/Fundamentals/em-pmsm","docId":"Fundamentals/em-pmsm","unlisted":false},{"type":"link","label":"Asynchronous Motors in Automotive Applications","href":"/BMS/docs/Fundamentals/em-asm","docId":"Fundamentals/em-asm","unlisted":false},{"type":"link","label":"Power Electronics in Electric Vehicles","href":"/BMS/docs/Fundamentals/pe","docId":"Fundamentals/pe","unlisted":false},{"type":"link","label":"Battery Management Systems in Electric Vehicles","href":"/BMS/docs/Fundamentals/bms","docId":"Fundamentals/bms","unlisted":false}],"href":"/BMS/docs/category/fundamentals"},{"type":"category","label":"Battery","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Importance of Battery and Battery Management Systems","href":"/BMS/docs/Battery/importance_of_battery","docId":"Battery/importance_of_battery","unlisted":false},{"type":"link","label":"Why Choose Lithium-Ion Batteries Over Alternatives?","href":"/BMS/docs/Battery/why_lithium_battery","docId":"Battery/why_lithium_battery","unlisted":false},{"type":"link","label":"How Does Lithium Battery Work?","href":"/BMS/docs/Battery/how_lithium_battery_works","docId":"Battery/how_lithium_battery_works","unlisted":false},{"type":"link","label":"Battery Terminology","href":"/BMS/docs/Battery/battery_terminology","docId":"Battery/battery_terminology","unlisted":false},{"type":"link","label":"Cycle Aging","href":"/BMS/docs/Battery/cycle_ageing","docId":"Battery/cycle_ageing","unlisted":false},{"type":"link","label":"Calendar Aging","href":"/BMS/docs/Battery/calendar_ageing","docId":"Battery/calendar_ageing","unlisted":false},{"type":"link","label":"Types of Chemistries vs. Battery Design Methodologies","href":"/BMS/docs/Battery/chemistries_vs_design","docId":"Battery/chemistries_vs_design","unlisted":false},{"type":"link","label":"Battery Form Factors","href":"/BMS/docs/Battery/form_factors","docId":"Battery/form_factors","unlisted":false},{"type":"link","label":"Cell Construction","href":"/BMS/docs/Battery/cell_construction","docId":"Battery/cell_construction","unlisted":false},{"type":"link","label":"Types of Cell Comparison","href":"/BMS/docs/Battery/cell_comparison","docId":"Battery/cell_comparison","unlisted":false},{"type":"link","label":"Cell to Cell Electrical Connection","href":"/BMS/docs/Battery/electrical_connection","docId":"Battery/electrical_connection","unlisted":false},{"type":"link","label":"Cell Types and Welding","href":"/BMS/docs/Battery/types_and_welding","docId":"Battery/types_and_welding","unlisted":false},{"type":"link","label":"Cylindrical Module","href":"/BMS/docs/Battery/cylindrical_module","docId":"Battery/cylindrical_module","unlisted":false},{"type":"link","label":"Pouch Module","href":"/BMS/docs/Battery/pouch_module","docId":"Battery/pouch_module","unlisted":false},{"type":"link","label":"Prismatic Module","href":"/BMS/docs/Battery/prismatic_module","docId":"Battery/prismatic_module","unlisted":false},{"type":"link","label":"Cell to Pack Integration","href":"/BMS/docs/Battery/cell_to_pack","docId":"Battery/cell_to_pack","unlisted":false},{"type":"link","label":"Cooling Mechanism for EV Batteries","href":"/BMS/docs/Battery/cooling_mechanism","docId":"Battery/cooling_mechanism","unlisted":false},{"type":"link","label":"Air Cooling and Heating","href":"/BMS/docs/Battery/ac-ht","docId":"Battery/ac-ht","unlisted":false},{"type":"link","label":"BMS Topologies","href":"/BMS/docs/Battery/topology","docId":"Battery/topology","unlisted":false}],"href":"/BMS/docs/category/battery"},{"type":"category","label":"Advanced Concepts","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Battery Standards","href":"/BMS/docs/Advanced-Concepts/battery_standards","docId":"Advanced-Concepts/battery_standards","unlisted":false},{"type":"link","label":"OCV vs SOC for Chemistries","href":"/BMS/docs/Advanced-Concepts/ocv_vs_soc","docId":"Advanced-Concepts/ocv_vs_soc","unlisted":false},{"type":"link","label":"SOA of Lithium Ion Battery","href":"/BMS/docs/Advanced-Concepts/soa_lithium","docId":"Advanced-Concepts/soa_lithium","unlisted":false}],"href":"/BMS/docs/category/advanced-concepts"},{"type":"category","label":"System and Algorithms","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Why We Need BMS?","href":"/BMS/docs/System_and_Algorithms/why_bms","docId":"System_and_Algorithms/why_bms","unlisted":false}],"href":"/BMS/docs/category/system-and-algorithms"},{"type":"category","label":"Glossary","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"A","href":"/BMS/docs/Glossary/A","docId":"Glossary/A","unlisted":false},{"type":"link","label":"B","href":"/BMS/docs/Glossary/B","docId":"Glossary/B","unlisted":false},{"type":"link","label":"C","href":"/BMS/docs/Glossary/C","docId":"Glossary/C","unlisted":false},{"type":"link","label":"R","href":"/BMS/docs/Glossary/R","docId":"Glossary/R","unlisted":false},{"type":"link","label":"S","href":"/BMS/docs/Glossary/S","docId":"Glossary/S","unlisted":false},{"type":"link","label":"Glossary","href":"/BMS/docs/Glossary/Sort","docId":"Glossary/Sort","unlisted":false}],"href":"/BMS/docs/category/glossary"},{"type":"link","label":"Battery Management Systems","href":"/BMS/docs/SUMMARY","docId":"SUMMARY","unlisted":false}]},"docs":{"Advanced-Concepts/battery_standards":{"id":"Advanced-Concepts/battery_standards","title":"Battery Standards","description":"Battery standards are essential to maintain safety, reliability, and compliance across various industries. They establish guidelines for handling potential hazards, ensuring consistent performance, and adhering to regulatory requirements. These standards provide a unified framework that governs design practices, performance benchmarks, and safety measures. By enforcing these guidelines, manufacturers and engineers can develop batteries that meet specific application requirements while mitigating risks.","sidebar":"tutorialSidebar"},"Advanced-Concepts/ocv_vs_soc":{"id":"Advanced-Concepts/ocv_vs_soc","title":"OCV vs SOC for Chemistries","description":"Introduction","sidebar":"tutorialSidebar"},"Advanced-Concepts/soa_lithium":{"id":"Advanced-Concepts/soa_lithium","title":"SOA of Lithium Ion Battery","description":"Introduction","sidebar":"tutorialSidebar"},"Battery/ac-ht":{"id":"Battery/ac-ht","title":"Air Cooling and Heating","description":"In the rapidly advancing field of electric mobility and energy storage, the Battery Thermal Management System (BTMS) plays a critical role in ensuring the optimal performance, longevity, and safety of battery packs. Among the various BTMS methodologies, air cooling and heating technology stands out as a widely adopted approach due to its simplicity, cost-effectiveness, and ease of integration. This document provides an in-depth exploration of air-based thermal management systems, detailing their architecture, variations (passive, active, and heat recovery systems), performance metrics, and inherent limitations. The insights presented are geared towards engineers, researchers, and industry professionals seeking to understand and implement effective BTMS solutions in electric vehicles (EVs) and other battery-dependent applications.","sidebar":"tutorialSidebar"},"Battery/battery_terminology":{"id":"Battery/battery_terminology","title":"Battery Terminology","description":"Battery Management Systems rely on specific technical terminologies to monitor, analyze, and optimize battery performance, safety, and reliability. A deep understanding of these terms is essential for professionals in electric vehicle (EV) development and energy storage systems. This guide provides an in-depth exploration of battery terminology, offering precise definitions, equations, examples, and their relevance to battery technology.","sidebar":"tutorialSidebar"},"Battery/calendar_ageing":{"id":"Battery/calendar_ageing","title":"Calendar Aging","description":"Calendar aging refers to the gradual decline in a battery\u2019s capacity, efficiency, and overall performance over time, even when the battery is not actively in use. This phenomenon significantly affects lithium-ion batteries, which are prevalent in electric vehicles (EVs), consumer electronics, and energy storage systems. Understanding the mechanisms, influencing factors, and mitigation strategies for calendar aging is crucial for enhancing battery longevity and performance, making it a pivotal consideration in the design and management of Battery Management Systems (BMS).","sidebar":"tutorialSidebar"},"Battery/cell_comparison":{"id":"Battery/cell_comparison","title":"Types of Cell Comparison","description":"In the landscape of modern technology, batteries play an indispensable role, powering everything from handheld consumer electronics to the burgeoning electric vehicle (EV) market. Central to the effective deployment of these energy storage systems is the Battery Management System (BMS), which oversees the safe and efficient operation of batteries by continuously monitoring and managing their performance parameters. This documentation delves into the diverse types of battery cells, their distinct form factors, and the criteria essential for selecting the appropriate cell type based on specific application requirements. It is meticulously crafted for engineers and industry professionals who seek both foundational insights and advanced technical knowledge in the realm of battery technology.","sidebar":"tutorialSidebar"},"Battery/cell_construction":{"id":"Battery/cell_construction","title":"Cell Construction","description":"---","sidebar":"tutorialSidebar"},"Battery/cell_to_pack":{"id":"Battery/cell_to_pack","title":"Cell to Pack Integration","description":"This document provides a comprehensive overview of the cell-to-pack (CTP) integration process, focusing on modular design, safety considerations, and manufacturing strategies for lithium-ion battery packs. The information is intended for engineers and industry professionals involved in battery system design and production.","sidebar":"tutorialSidebar"},"Battery/chemistries_vs_design":{"id":"Battery/chemistries_vs_design","title":"Types of Chemistries vs. Battery Design Methodologies","description":"Battery chemistries fundamentally determine the performance, safety, and suitability of batteries for various applications. Among these, lithium-ion (Li-ion) batteries have become the industry standard due to their exceptional energy density, versatility, and performance. This section explores the different Li-ion battery chemistries, compares their key parameters, examines design trade-offs, and highlights future trends shaping battery technology.","sidebar":"tutorialSidebar"},"Battery/cooling_mechanism":{"id":"Battery/cooling_mechanism","title":"Cooling Mechanism for EV Batteries","description":"This document provides an in-depth analysis of cooling mechanisms for electric vehicle (EV) batteries, focusing on their design, efficiency, and application. Effective thermal management is essential to ensure battery performance, longevity, and safety. This guide explores current technologies, challenges, and future innovations in battery cooling systems.","sidebar":"tutorialSidebar"},"Battery/cycle_ageing":{"id":"Battery/cycle_ageing","title":"Cycle Aging","description":"Cycle aging refers to the progressive decline in a battery\u2019s capacity and performance resulting from repeated charge-discharge cycles. While this degradation is an inherent aspect of battery usage, its impact can be effectively managed through optimal usage practices and sophisticated battery management systems (BMS). Understanding the factors that influence cycle aging and implementing strategies to mitigate them are crucial for enhancing battery longevity, especially in applications such as electric vehicles (EVs), renewable energy storage, and consumer electronics.","sidebar":"tutorialSidebar"},"Battery/cylindrical_module":{"id":"Battery/cylindrical_module","title":"Cylindrical Module","description":"This document provides a detailed overview of cylindrical battery modules, including their construction, electrical and mechanical connections, cooling mechanisms, and implementation in battery packs, particularly for electric vehicles (EVs). The focus is on delivering foundational and technical insights to engineers and industry professionals involved in battery system design.","sidebar":"tutorialSidebar"},"Battery/electrical_connection":{"id":"Battery/electrical_connection","title":"Cell to Cell Electrical Connection","description":"In the intricate architecture of battery systems, the electrical connections between individual cells are pivotal in determining the overall performance, energy capacity, and safety of the battery pack. These connections orchestrate how voltage, current, and thermal dynamics are managed within the system, directly influencing its scalability and suitability for specific applications. This documentation delves into the fundamental principles, sophisticated design methodologies, and critical considerations involved in establishing cell-to-cell electrical connections. By exploring these aspects in depth, we highlight their profound impact on battery performance and reliability, providing engineers and industry professionals with the expertise needed to design and optimize advanced battery systems.","sidebar":"tutorialSidebar"},"Battery/form_factors":{"id":"Battery/form_factors","title":"Battery Form Factors","description":"Batteries and Battery Management Systems (BMS) are critical components in modern energy storage and management systems. Their applications span a wide range, including consumer electronics, electric vehicles (EVs), and large-scale energy storage solutions. Understanding battery form factors\u2014their physical shapes and dimensions\u2014and their associated advantages and limitations is essential for engineers and designers involved in creating efficient and reliable battery packs.","sidebar":"tutorialSidebar"},"Battery/how_lithium_battery_works":{"id":"Battery/how_lithium_battery_works","title":"How Does Lithium Battery Work?","description":"Lithium-ion batteries are ubiquitous in modern applications due to their high energy density, rechargeability, and long lifespan. They are integral to devices ranging from smartphones to electric vehicles (EVs), enabling the transition toward renewable energy systems.","sidebar":"tutorialSidebar"},"Battery/importance_of_battery":{"id":"Battery/importance_of_battery","title":"Importance of Battery and Battery Management Systems","description":"Battery Management Systems are vital for ensuring the safe, efficient, and durable operation of battery packs, especially in electric vehicles (EVs). As the control center of the battery system, the BMS plays a critical role in monitoring, protecting, and managing the performance of high-voltage batteries. These systems are used extensively in passenger vehicles, light commercial vehicles (LCVs), buses, and other electrified applications. This document offers an in-depth exploration of the importance of batteries, their management systems, and the technical and operational frameworks that support them.","sidebar":"tutorialSidebar"},"Battery/pouch_module":{"id":"Battery/pouch_module","title":"Pouch Module","description":"Pouch cells are a popular choice for applications requiring high energy density and lightweight designs. Unlike cylindrical cells, pouch cells are encased in a flexible aluminum-laminated film, allowing for more efficient space utilization. However, this form factor introduces unique challenges, such as susceptibility to swelling under certain conditions. This document outlines the design, assembly, and operational considerations for pouch battery modules.","sidebar":"tutorialSidebar"},"Battery/prismatic_module":{"id":"Battery/prismatic_module","title":"Prismatic Module","description":"Prismatic cells are one of the simplest formats for constructing battery packs. Encased in a rigid metal or hard plastic enclosure, prismatic cells offer a flat and compact design, making them ideal for high-density applications. Their modularity and ease of assembly contribute to their popularity in automotive and stationary energy storage solutions.","sidebar":"tutorialSidebar"},"Battery/topology":{"id":"Battery/topology","title":"BMS Topologies","description":"In the dynamic and rapidly evolving landscape of energy storage and electric mobility, the Battery Management System (BMS) serves as the cornerstone for ensuring the safe, efficient, and reliable operation of battery packs. A critical aspect of BMS design is its topology, which defines the structural arrangement and interconnection of its components to monitor, control, and manage individual battery cells effectively. This document provides an in-depth exploration of various BMS topologies, including centralized, modular, master-slave, and distributed configurations. It offers a detailed and rigorous analysis of their designs, advantages, disadvantages, and suitable applications, catering to engineers, researchers, and industry professionals involved in battery technology and management.","sidebar":"tutorialSidebar"},"Battery/types_and_welding":{"id":"Battery/types_and_welding","title":"Cell Types and Welding","description":"Battery cells are the fundamental building blocks of energy storage systems, and their types dictate the design and assembly techniques required for robust and efficient battery management systems (BMS). The main types of battery cells include:","sidebar":"tutorialSidebar"},"Battery/why_lithium_battery":{"id":"Battery/why_lithium_battery","title":"Why Choose Lithium-Ion Batteries Over Alternatives?","description":"Lithium-ion batteries have become a cornerstone of modern energy storage solutions, excelling in applications that demand high energy efficiency, superior performance, and dependable reliability. Their widespread adoption is the result of a blend of technological advancements, economic benefits, and environmental considerations that outpace other battery technologies. This section explores the key factors that make lithium-ion batteries the preferred choice in today\u2019s diverse systems.","sidebar":"tutorialSidebar"},"Fundamentals/bms":{"id":"Fundamentals/bms","title":"Battery Management Systems in Electric Vehicles","description":"Battery Management Systems (BMS) are the unsung heroes of electric and hybrid vehicles, ensuring that batteries operate safely, efficiently, and reliably. As electric mobility gains momentum, the role of BMS becomes increasingly critical in optimizing battery performance, extending lifespan, and safeguarding against potential hazards. This comprehensive documentation provides an in-depth exploration of Battery Management Systems in the automotive sector, covering their fundamentals, functions, components, requirements, challenges, technological advancements, and future prospects.","sidebar":"tutorialSidebar"},"Fundamentals/bms-in-ev":{"id":"Fundamentals/bms-in-ev","title":"Battery Management Systems in Electric Vehicles","description":"Electric Vehicles (EVs) are at the forefront of the global shift towards sustainable transportation. Central to their functionality and reliability is the Battery Management System (BMS), an intricate electronic system that governs the performance, safety, and longevity of lithium-ion battery packs. This documentation delves into the pivotal role of BMS in EVs, exploring its core functionalities, design challenges, and the innovative advancements shaping its future.","sidebar":"tutorialSidebar"},"Fundamentals/components":{"id":"Fundamentals/components","title":"Components of Electric Vehicles","description":"Electric Vehicles (EVs) represent a pivotal advancement in the automotive industry, driven by the urgent need for sustainable transportation solutions. At the heart of every EV lies a sophisticated interplay of key components: the Electric Machine, Power Electronics, and Battery. Central to the harmonious operation of these elements is the Battery Management System (BMS), which orchestrates their interactions to ensure optimal performance, safety, and longevity. This documentation provides an in-depth exploration of each component, their interdependencies, and the innovations shaping the future of electric mobility.","sidebar":"tutorialSidebar"},"Fundamentals/em-asm":{"id":"Fundamentals/em-asm","title":"Asynchronous Motors in Automotive Applications","description":"Asynchronous Motors (ASMs), also known as Induction Motors, have long been a cornerstone in various industrial applications due to their robustness, simplicity, and cost-effectiveness. In the automotive industry, ASMs play a crucial role, particularly in electric vehicles (EVs) and hybrid electric vehicles (HEVs), where their durability and adaptability make them invaluable for both propulsion and auxiliary systems. This comprehensive documentation delves into the principles of operation, applications, advantages, challenges, technological advancements, and future prospects of ASMs in the automotive sector.","sidebar":"tutorialSidebar"},"Fundamentals/em-pmsm":{"id":"Fundamentals/em-pmsm","title":"Permanent Magnet Synchronous Motors in Automotive Applications","description":"Permanent Magnet Synchronous Motors (PMSMs) have emerged as a pivotal technology in the automotive industry, particularly within electric vehicles (EVs) and hybrid electric vehicles (HEVs). Leveraging permanent magnets within their rotors, PMSMs offer unparalleled efficiency, high power density, and reliability, making them a preferred choice for modern automotive propulsion and auxiliary systems. This documentation provides a comprehensive exploration of PMSMs, detailing their principles of operation, applications, advantages, challenges, and future advancements within the automotive sector.","sidebar":"tutorialSidebar"},"Fundamentals/pe":{"id":"Fundamentals/pe","title":"Power Electronics in Electric Vehicles","description":"Power electronics serve as the backbone of electric vehicles (EVs), bridging the gap between energy sources, storage systems, and electric drives. By efficiently converting and controlling electrical power, power electronics enable precise and reliable operation of EVs, enhancing performance, efficiency, and sustainability. As the automotive industry accelerates its shift toward electrification, advancements in power electronics are pivotal in driving innovation and meeting the evolving demands of modern transportation. This documentation provides an in-depth exploration of power electronics in EVs, covering their core functions, key components, advantages, challenges, technological advancements, comparative analysis, future trends, and environmental considerations.","sidebar":"tutorialSidebar"},"Glossary/A":{"id":"Glossary/A","title":"A","description":"-","sidebar":"tutorialSidebar"},"Glossary/B":{"id":"Glossary/B","title":"B","description":"- Battery Management System (BMS): An electronic system that manages a rechargeable battery (cell or battery pack), ensuring safe operation, optimal performance, and longevity.","sidebar":"tutorialSidebar"},"Glossary/C":{"id":"Glossary/C","title":"C","description":"- Controller Area Network (CAN) Bus: A robust vehicle bus standard designed to allow microcontrollers and devices to communicate with each other without a host computer.","sidebar":"tutorialSidebar"},"Glossary/R":{"id":"Glossary/R","title":"R","description":"- Regenerative Braking: A mechanism in electric and hybrid vehicles that recaptures kinetic energy during braking and converts it into electrical energy for storage in the battery.","sidebar":"tutorialSidebar"},"Glossary/S":{"id":"Glossary/S","title":"S","description":"- State of Charge (SoC): A measure of the remaining charge in a battery relative to its capacity.","sidebar":"tutorialSidebar"},"Glossary/Sort":{"id":"Glossary/Sort","title":"Glossary","description":"- Permanent Magnet Synchronous Motor (PMSM): An electric motor that uses permanent magnets in the rotor and operates synchronously with the stator\'s rotating magnetic field.","sidebar":"tutorialSidebar"},"SUMMARY":{"id":"SUMMARY","title":"Battery Management Systems","description":"Battery Management Systems","sidebar":"tutorialSidebar"},"System_and_Algorithms/why_bms":{"id":"System_and_Algorithms/why_bms","title":"Why We Need BMS?","description":"A Battery Management System (BMS) is an integral component of any modern battery-powered system, especially those utilizing lithium-ion technology. As the \\"brain\\" of the battery, the BMS ensures that the battery operates safely, efficiently, and reliably. Its presence is mandatory in a wide range of applications, from consumer electronics like smartphones and laptops to critical systems like electric vehicles (EVs) and renewable energy storage.","sidebar":"tutorialSidebar"}}}}')}}]);